// ----------------------------------------------------------------------
// Functions for testing events
// ----------------------------------------------------------------------

void PassiveCommandsTesterBase ::
  dispatchEvents(
      FwEventIdType id,
      Fw::Time& timeTag,
      const Fw::LogSeverity severity,
      Fw::LogBuffer& args
  )
{
  args.resetDeser();

  const U32 idBase = this->getIdBase();
  FW_ASSERT(id >= idBase, id, idBase);

  switch ((id - idBase)) {
    default: {
      FW_ASSERT(0, id);
      break;
    }
  }
}

