// ----------------------------------------------------------------------
// Functions for testing commands
// ----------------------------------------------------------------------

void PassiveSerialTesterBase ::
  cmdResponseIn(
      FwOpcodeType opCode,
      U32 cmdSeq,
      Fw::CmdResponse response
  )
{
  CmdResponse e = { opCode, seq, response };
  this->cmdResponseHistory->push_back(e);
}

void PassiveSerialTesterBase ::
  sendRawCmd(
      FwOpcodeType opCode,
      U32 cmdSeq,
      Fw::CmdBufferArg& args
  )
{
  const U32 idBase = this->getIdBase();
  FwOpcodeType _opcode = opcode + idBase;
  if (this->m_to_cmdIn[0].isConnected()) {
    this->m_to_cmdIn[0].invoke(
      _opcode,
      cmdSeq,
      args
    );
  }
  else {
    printf("Test Command Output port not connected!\n");
  }
}

void PassiveSerialTesterBase ::
  sendCmd_CMD_SYNC(
      const NATIVE_INT_TYPE instance,
      U32 cmdSeq
  )
{
  // Call output command port
  FwOpcodeType _opcode;
  const U32 idBase = this->getIdBase();
  _opcode = PassiveSerialComponentBase::OPCODE_CMD_SYNC + idBase;

  if (this->m_to_cmdIn[0].isConnected()) {
    this->m_to_cmdIn[0].invoke(
      _opcode,
      cmdSeq,
      args
    );
  }
  else {
    printf("Test Command Output port not connected!\n");
  }
}

void PassiveSerialTesterBase ::
  sendCmd_CMD_SYNC_PRIMITIVE(
      const NATIVE_INT_TYPE instance,
      U32 cmdSeq,
      U32 u32,
      F32 f32,
      bool b
  )
{
  // Serialize arguments
  Fw::CmdArgBuffer buf;
  Fw::SerializeStatus _status;
  _status = buf.serialize(u32);
  FW_ASSERT(
    status == FW::FW_SERIALIZE_OK,
    static_cast<FwAssertArgType>(_status)
  );

  _status = buf.serialize(f32);
  FW_ASSERT(
    status == FW::FW_SERIALIZE_OK,
    static_cast<FwAssertArgType>(_status)
  );

  _status = buf.serialize(b);
  FW_ASSERT(
    status == FW::FW_SERIALIZE_OK,
    static_cast<FwAssertArgType>(_status)
  );

  // Call output command port
  FwOpcodeType _opcode;
  const U32 idBase = this->getIdBase();
  _opcode = PassiveSerialComponentBase::OPCODE_CMD_SYNC_PRIMITIVE + idBase;

  if (this->m_to_cmdIn[0].isConnected()) {
    this->m_to_cmdIn[0].invoke(
      _opcode,
      cmdSeq,
      args
    );
  }
  else {
    printf("Test Command Output port not connected!\n");
  }
}

